INSERT INTO `gtype1` (`id`, `gen`, `de`, `imga`, `followercount`) VALUES
(4, 'Comedy', '<p>Comedy refers to any such works, which are humurous and are laughter inducing.</p>', 'genres/comedy.png', NULL),
(3, 'Fiction', '<p>Fiction&nbsp;is the classification for any story or similar work derived from imagination.Such works, are not exactly based on history or on facts.</p>', 'genres/fiction.jpg', 1),
(5, 'Drama', '<p>Drama is a mode of fiction represented as performance. This genre is also called play.</p>', 'genres/drama.jpg', NULL),
(6, 'Horror', '<p>Horror is a genre of fiction which is intended to, or has the capacity to frighten, scare, disgust, or startle their readers by inducing feelings of horror or terror.</p>', 'genres/horror.jpg', 1),
(7, 'Non-Fiction', '<p>Non-fiction or nonfiction is content whose creator, in good faith,&nbsp;assumes responsibility for the truth or accuracy of the events, people, or information presented.</p>', 'genres/nonfiction.jpg', NULL),
(8, 'Realistic Fiction', '<p>Realistic fiction typically involves a story whose basic setting is real and whose events could feasibly happen in a real-world setting.</p>', 'genres/realfiction.jpg', NULL),
(9, 'Romance', '<p>Content belonging to this genre, generally focusses on the relationship or love between two people.</p>', 'genres/romance.jpg', 1),
(10, 'Satire', '<p>Satire is a genre of literature, in which vices, follies, abuses, and shortcomings are held up to ridicule, ideally with the intent of shaming individuals, corporations, government, or society itself into improvement.</p>', 'genres/satire.jpg', NULL),
(11, 'Tragedy', '<p>Tragedy is a form of literature which acompanies human suffereing that invokes an accompanying catharsis or pleasure in the readers.</p>', 'genres/tragedy.jpg', NULL),
(12, 'Tragicomedy', '<p>This is a genre, that is a blend of both comedy and tragic types.</p>', 'genres/tragicomedy.png', NULL),
(13, 'Fantasy', '<p>Fantasy is a fiction genre<span style="font-size: xx-small;">&nbsp;</span>set in an imaginary universe, often but not always without any locations, events, or people from the real world.</p>', 'genres/fantasy.jpg', NULL),
(14, 'Crime', '<p>Crime fiction is the literary genre&nbsp;that fictionalises crimes, their detection, criminals, and their motives.</p>', 'genres/crime.jpg', NULL),
(15, 'Fan-fiction', '<p>Fiction written by a fan of, and featuring characters from, a particular TV series, movie, or book.</p>', 'genres/fanfic.jpg', NULL),
(16, 'Historical fiction', '<p>Stories with fictional characters and events in a historical setting.</p>', 'genres/histfiction.jpg', NULL),
(17, 'Legend', '<p>Story of a national or folk hero, that has a basis in fact but also includes imaginative material.</p>', 'genres/legend.png', NULL),
(18, 'Magical Realism', '<p>Story where magical or unreal elements play a natural part in an otherwise realistic environment.</p>', 'genres/magic.jpg', NULL),
(19, 'Metafiction', '<p>Metafiction is a literary device&nbsp;used self-consciously and systematically to draw attention to a work\'s status as a work of imagination,&nbsp;rather than reality. It poses questions about the relationship between&nbsp;and reality, usually using irony and self-reflection.</p>', 'genres/metafiction.jpg', NULL),
(20, 'Mystery', '<p>Mystery fiction is a genre of fiction usually involving a mysterious death or a crime to be solved.</p>', 'genres/mystery.jpg', NULL),
(21, 'Mythology', '<p>Legend or traditional narrative, often based in part on historical events, that reveals human behavior and natural phenomena by its symbolism; often pertaining to the actions of the gods.</p>', 'genres/mythology.jpg', 1),
(22, 'Mythopoeia', '<p>Fiction in which characters from religious mythology, traditional myths, folklore and/or history are recast into a re-imagined realm created by the author.</p>', 'genres/mythopoeia.jpg', NULL),
(23, 'Science Fiction', '<p>Stories based on the impact of actual, imagined, or potential science, usually set in the future or on other planets.</p>', 'genres/scifi.jpg', 1),
(24, 'Short Story', '<p>Diction of such brevity that it supports no subplots.</p>', 'genres/shortstory.jpg', NULL),
(25, 'Suspense/Thriller', '<p>Stories which keep the readers on the edge of their seats.</p>', 'genres/thriller.jpg', NULL),
(26, 'Journalism', '<p>Reporting on news and current events.</p>', 'genres/journalism.jpg', NULL),
(27, 'Western', '<p>Set in the American Old West frontier and typically set in the late eighteenth to late nineteenth century.</p>', 'genres/western.jpg', NULL),
(30, 'Blog', 'A regularly updated or an informative article written by an individual.', 'genres/blog.jpg', NULL);

